import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  Box,
  Paper,
  Divider,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Button
} from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, BarChart, Bar, XAxis, YAxis, Tooltip } from 'recharts';

function Dashboard() {
  const [runningDiscoveries, setRunningDiscoveries] = useState([]);
  const [stats, setStats] = useState({
    serverCount: 0,
    discoveryCount: 0,
    successRate: 0,
    regions: {},
    lastDiscovery: null,
    recentDiscoveries: [],
    servers: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/stats');
      if (!response.ok) {
        throw new Error(`API returned ${response.status}: ${response.statusText}`);
      }
      const data = await response.json();
      setStats(data);
      setError(null);
    } catch (err) {
      console.error('Error fetching stats:', err);
      setError('Failed to load dashboard data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Handle running a discovery on a server
  const handleRunDiscovery = async (serverId) => {
    try {
      // Add the server ID to the running discoveries list
      setRunningDiscoveries([...runningDiscoveries, serverId]);

      // Make API call to start discovery
      const response = await fetch(`/api/servers/${serverId}/discover`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`API returned ${response.status}: ${response.statusText}`);
      }

      // Refresh stats after a short delay to allow the discovery to start
      setTimeout(() => {
        fetchStats();
        // Remove the server from running discoveries
        setRunningDiscoveries(runningDiscoveries.filter(id => id !== serverId));
      }, 2000);
    } catch (err) {
      console.error(`Error starting discovery for server ${serverId}:`, err);
      setError(`Failed to start discovery for server ID ${serverId}`);
      // Remove the server from running discoveries
      setRunningDiscoveries(runningDiscoveries.filter(id => id !== serverId));
    }
  };

  // Debug render
  console.log('Dashboard rendering with stats:', stats, 'loading:', loading, 'error:', error);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ mt: 4 }}>
        <Typography color="error" variant="h6">
          Error: {error}
        </Typography>
      </Box>
    );
  }

  if (!stats) {
    return (
      <Box sx={{ mt: 4 }}>
        <Typography color="error" variant="h6">
          No data available
        </Typography>
      </Box>
    );
  }

  // Prepare data for charts
  const successRateData = [
    { name: 'Success', value: stats.successRate },
    { name: 'Failure', value: 100 - stats.successRate },
  ];

  const regionData = Object.entries(stats.regions || {}).map(([region, count]) => ({
    name: region || 'Unknown',
    count,
  }));

  const COLORS = ['#00C49F', '#FF8042', '#0088FE', '#FFBB28', '#8884d8'];

  return (
    <Box sx={{ 
      backgroundColor: 'background.paper', 
      padding: 2, 
      borderRadius: 1,
      boxShadow: 1
    }}>
      <Typography variant="h4" gutterBottom>
        Server Discovery Dashboard
      </Typography>

      {/* Stats Cards */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Servers
              </Typography>
              <Typography variant="h3" color="primary">
                {stats.serverCount}
              </Typography>
              <Typography variant="body2" color="textSecondary">
                <Link to="/servers">View all servers</Link>
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Discoveries
              </Typography>
              <Typography variant="h3" color="primary">
                <Link 
                  to="/discoveries" 
                  style={{ 
                    color: 'inherit', 
                    textDecoration: 'none',
                    '&:hover': {
                      textDecoration: 'underline',
                      color: 'primary.main'
                    }
                  }}
                >
                  {stats.discoveryCount}
                </Link>
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Total discovery operations
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Success Rate
              </Typography>
              <Typography variant="h3" color="primary">
                {stats.successRate.toFixed(1)}%
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Discovery success rate
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Charts and Tables */}
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2, height: 300 }}>
            <Typography variant="h6" gutterBottom>
              Success Rate
            </Typography>
            <ResponsiveContainer width="100%" height="90%">
              <PieChart>
                <Pie
                  data={successRateData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(1)}%`}
                >
                  {successRateData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={index === 0 ? '#00C49F' : '#FF8042'} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2, height: 300 }}>
            <Typography variant="h6" gutterBottom>
              Servers by Region
            </Typography>
            <ResponsiveContainer width="100%" height="90%">
              <BarChart data={regionData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="count" fill="#8884d8">
                  {regionData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          </Paper>
        </Grid>
        <Grid item xs={12}>
          <Paper sx={{ p: 2, display: 'flex', flexDirection: 'column' }}>
            <Typography component="h2" variant="h6" color="primary" gutterBottom>
              Recent Discoveries
            </Typography>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Hostname</TableCell>
                    <TableCell>Port</TableCell>
                    <TableCell>Region</TableCell>
                    <TableCell>OS Type</TableCell>
                    <TableCell>Tags</TableCell>
                    <TableCell>Last Discovery</TableCell>
                    <TableCell width="150">Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {stats.servers && stats.servers.length > 0 ? (
                    stats.servers.map((server) => (
                      <TableRow key={server.id}>
                        <TableCell>
                          <Link to={`/servers/${server.id}`}>{server.hostname}</Link>
                        </TableCell>
                        <TableCell>{server.port}</TableCell>
                        <TableCell>{server.region}</TableCell>
                        <TableCell>
                          {server.port === 22 ? (
                            <Chip label="Linux" color="success" size="small" />
                          ) : (
                            <Chip label="Windows" color="primary" size="small" />
                          )}
                        </TableCell>
                        <TableCell>
                          {server.tags && server.tags.map((tag) => (
                            <Chip 
                              key={tag.key} 
                              label={`${tag.key}: ${tag.value}`} 
                              size="small" 
                              sx={{ mr: 0.5, mb: 0.5 }}
                            />
                          ))}
                        </TableCell>
                        <TableCell>
                          {server.last_discovery ? new Date(server.last_discovery).toLocaleString() : 'Never'}
                        </TableCell>
                        <TableCell>
                          <Button 
                            variant="contained" 
                            size="small" 
                            onClick={() => handleRunDiscovery(server.id)}
                            disabled={runningDiscoveries.includes(server.id)}
                          >
                            {runningDiscoveries.includes(server.id) ? 'Running...' : 'Run Discovery'}
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={7} align="center">
                        No servers available
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}

export default Dashboard;
