import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Box,
  Typography,
} from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import StorageIcon from '@mui/icons-material/Storage';
import CodeIcon from '@mui/icons-material/Code';

function Sidebar({ open }) {
  const location = useLocation();
  const drawerWidth = open ? 240 : 60;

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
          transition: 'width 0.2s',
          overflowX: 'hidden',
        },
      }}
    >
      <Box sx={{ height: 64 }} /> {/* Spacer for AppBar */}
      <Box sx={{ overflow: 'auto' }}>
        <List>
          <ListItem
            button
            component={Link}
            to="/"
            selected={isActive('/')}
          >
            <ListItemIcon>
              <DashboardIcon />
            </ListItemIcon>
            {open && <ListItemText primary="Dashboard" />}
          </ListItem>
          <ListItem
            button
            component={Link}
            to="/servers"
            selected={isActive('/servers')}
          >
            <ListItemIcon>
              <StorageIcon />
            </ListItemIcon>
            {open && <ListItemText primary="Servers" />}
          </ListItem>
          <ListItem
            button
            component={Link}
            to="/query"
            selected={isActive('/query')}
          >
            <ListItemIcon>
              <CodeIcon />
            </ListItemIcon>
            {open && <ListItemText primary="SQL Query" />}
          </ListItem>
        </List>
      </Box>
    </Drawer>
  );
}

export default Sidebar;
